let ReportingPeriod = startofmonth(datetime_add('month', -1, now()));
let CurrentMonth = startofmonth(now())-1tick;
let TotalCases = toscalar(KPISupportData
    | where CreatedDateTime >= ReportingPeriod and CreatedDateTime < CurrentMonth
    | count);
let data = cluster('https://U360sec.kusto.windows.net').database('KPISupportData').Selfhelpdeflection
| where ProductId == 16602
| where usageTime >= ReportingPeriod and usageTime < startofmonth(now())
| where IsAbandoned == "Yes" and IsDeflected == "Yes" and isnotempty(ArticleId);
let deflectCount = toscalar(data
    | count);
data
| summarize dcount(WorkflowId) by SolutionType
| extend PctOfTotal=round((todouble(dcount_WorkflowId)/TotalCases) * 100,2)
| extend PctofDeflections=round((todouble(dcount_WorkflowId)/deflectCount) * 100,2)

